import React, { Component } from 'react';

class CommentForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            userName: '',
            userComment: '',
            articleId: this.props.articleID
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(e) {
        const target = e.target;
        const inputName = target.name;
        const inputValue = target.value;
        this.setState({
            [inputName]: inputValue
        });
    }
    handleSubmit(e) {
        console.log(e);
        var params = {
            userName: this.state.userName,
            userComment: this.state.userComment,
        };

        var formData = new FormData();
        for (var k in params) {
            formData.append(k, params[k]);
        }
        fetch('http://localhost:3000/comments/'+this.props.articleId, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: JSON.stringify({
                firstParam: 'yourValue',
                secondParam: 'yourOtherValue',
            })
        })
        e.preventDefault();
    }
    render() {
        return (
            <div className="row__wrapper form__wrapper">
                <p className="form__title">Deja tu comentario</p>
                <form onSubmit={this.handleSubmit}>
                    <input name="userName" type="hidden" value={ this.state.userName } />
                    <textarea name="userComment" value={this.state.userComment} onChange={this.handleChange}></textarea>
                    <input name="articleId" type="hidden" value={ this.state.articleId } />
                    <input type="submit" value="submit" />
                </form>
            </div>
        );
    }
}

export default CommentForm
