import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class ArticleView extends Component {
    state = {
        article:[]
    }
    componentDidMount(){
        fetch('http://localhost:3000/articles'+this.props.match.params.articleId)
        .then(res => res.json())
        .then((data) => {
            this.setState({ articles: data })
        })
        .catch(console.log)
    }
    // onClick={this.showArticle.bind(this, this.props)}
    showArticle = (e, data) => {
        console.log(e.article._id);
    }

    render() {
        return (
            <div className="article__item" data-article-id={this.props.article._id}>
                <div className="content__wrapper">
                    <p className="article__component article__name">{this.props.article.name}</p>
                    <p className="article__component article__date">{(new Date(this.props.article.Created_date)).toLocaleDateString('es-CO', { year: 'numeric', month: 'long', day: 'numeric' })}</p>
                    <p className="article__component article__body">{this.props.article.body}</p>
                </div>
            </div>
        );
    }
}

export default ArticleView;
