import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import ArticleView from './ArticleView.js';

class ArticleCard extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <Router>
                <div className="article__item" data-article-id={this.props.article._id}>
                    <Link to={'/article/'+this.props.article._id } className="article__link" >
                    <div className="content__wrapper">
                        <p className="article__component article__name">{this.props.article.name}</p>
                        <p className="article__component article__date">{(new Date(this.props.article.Created_date)).toLocaleDateString('es-CO', { year: 'numeric', month: 'long', day: 'numeric' })}</p>
                        <p className="article__component article__body">{this.props.article.body}</p>
                    </div>
                    </Link>
                </div>
                <Route path={"/article/:articleId"} component={ArticleView} />
            </Router>
        );
    }
}

export default ArticleCard;
