import React, { Component } from 'react';

class CommentForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            userName: '',
            userComment: '',
            articleId: '',
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    componentDidMount(){
        this.setState({
            articleId: this.props.articleID
        })
        console.log(this.state)
    }
    handleChange(e) {
        const target = e.target;
        const inputName = target.name;
        const inputValue = target.value;
        this.setState({
            [inputName]: inputValue
        });
    }
    handleSubmit(e) {

        var params = {
            User: this.state.userName,
            body: this.state.userComment,
            article: this.props.articleId
        };

        var formData = new FormData();
        for (var k in params) {
            formData.append(k, params[k]);
        }
        /*
        fetch('http://localhost:3000/comments/'+this.props.articleId, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: formData
        }).then(res => res.json())
        .then((data) => {
            console.log(data);
        })
        .catch(console.log)
        */

        e.preventDefault();
    }
    render() {
        return (
            <div className="row__wrapper form__wrapper">
                <p className="form__title">Deja tu comentario</p>
                <form onSubmit={this.handleSubmit}>
                    <input name="userName" type="hidden" value={ this.state.userName } />
                    <textarea name="userComment" value={this.state.userComment} onChange={this.handleChange}></textarea>
                    <input name="articleId" type="hidden" value={ this.props.articleID } />
                    <input type="submit" value="submit" />
                </form>
            </div>
        );
    }
}

export default CommentForm
