import React, { Component } from 'react';

class CommentForm extends Component {
    constructor(props){
        super(props);
        this.state = {userValue: '',
                      commentValue: '',
                     };
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event) {
        this.setState({commentValue: event.target.value});
    }
    handleSubmit(e) {
        console.log(e);
    }
    render() {
        return (
            <div className="row__wrapper form__wrapper">
                <p className="form__title">Deja tu comentario</p>
                <form onSubmit={this.handleSubmit}>
                    <input type="hidden" value={ this.state.userValue } />
                    <textarea value={this.state.commentValue} onChange={this.handleChange}></textarea>
                    <input type="submit" value="submit" />
                </form>
            </div>
        );
    }
}

export default CommentForm
