import React, { Component } from 'react';

class CommentForm extends Component {
    constructor(props){
        super(props);
        this.state = {userName: '', userComment: ''};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(e) {
        inputName = e.name;
        inputValue = e.value;
        this.setState({[inputName]: inputValue});
    }
    handleSubmit(e) {
        console.log(e);
        e.preventDefault();
    }
    render() {
        return (
            <div className="row__wrapper form__wrapper">
                <p className="form__title">Deja tu comentario</p>
                <form onSubmit={this.handleSubmit}>
                    <input name="userName" type="hidden" value={ this.state.userName } />
                    <textarea name="userComment" value={this.state.userComment} onChange={this.handleChange}></textarea>
                    <input type="submit" value="submit" />
                </form>
            </div>
        );
    }
}

export default CommentForm
