import React, { Component } from 'react';
import CommentForm from './CommentForm.js';
import GoogleLogin from 'react-google-login';

class Comments extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newComment: false
        }
        this.insertNewComment = this.insertNewComment.bind(this);
        console.log(GoogleLogin.props);
    }
    insertNewComment(item) {
        this.props.comments.push(item);
        console.log(this.props.comments);
        this.setState({
            newComment: true
        })
    }
    render() {
        return (
            <div className="col__wrapper article__comments">
                <h2>Commentarios</h2>
                {this.props.comments.map((comment) => (
                    <div className="comment__wrapper">
                        <div className="comment__author">
                            <p>{comment.User}</p>
                        </div>
                        <div className="comment__body">
                            <p>{comment.body}</p>
                        </div>
                    </div>
                ))}
                <CommentForm articleID={this.props.articleId} action={this.insertNewComment.bind(this)} />
            </div>
        );
    }
}

export default Comments
