import React, { Component } from 'react';
import CommentForm from './CommentForm.js';

class Comments extends Component {
    constructor(props) {
        super(props);
        this.state = {
            comments: this.props.comments
        }
        console.log(this.state.comments)
        this.insertNewComment = this.insertNewComment.bind(this);
        // this.handleChange = this.handleChange.bind(this);
        this.state = {
            newComment: {}
        };
    }
    insertNewComment(item) {
        this.setState({
            newComment: item
        })
        this.displayData.push(
            <div className="comment__wrapper">
                <div className="comment__author">
                    <p>{this.state.newComment.User}</p>
                </div>
                <div className="comment__body">
                    <p>{this.state.newComment.body}</p>
                </div>
            </div>
        );
    }
    render() {
        return (
            <div className="col__wrapper article__comments">
                <h2>Commentarios</h2>
            {this.props.comments.map((comment) => (
                <div className="comment__wrapper">
                    <div className="comment__author">
                        <p>{comment.User}</p>
                    </div>
                    <div className="comment__body">
                        <p>{comment.body}</p>
                    </div>
                </div>
            ))}
                <CommentForm articleID={this.props.articleId} action={this.insertNewComment.bind(this)} />
            </div>
        );
    }
}

export default Comments
