import React, { Component } from 'react';

class Comments extends Component {
    state = {
        comments:[]
    }
    componentDidMount(){
        fetch('http://localhost:3000/comments/'+this.props.articleId)
        .then(res => res.json())
        .then((data) => {
            this.setState({ comments: data })
            console.log(data)
        })
        .catch(console.log)
    }
    render() {
        return (
            <div className="row__wrapper article__comments">
            {this.state.comments.map((comment) => (
                comment._id
            ))}
            </div>
        );
    }
}

export default Comments
