import React, { Component } from 'react';
import Login from './Login.js';
import Logout from './Logout.js';

class CommentForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            userName: '',
            userComment: '',
            loggedIn: false
        };

        this.handleLogin = this.handleLogin.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleLogin(e){
        console.log(e);
        this.setState({loggedIn: e})
    }
    handleChange(e) {
        const target = e.target;
        const inputName = target.name;
        const inputValue = target.value;
        this.setState({
            [inputName]: inputValue
        });
    }
    handleSubmit(e) {

        e.preventDefault();

        var usrName = '';

        if(localStorage.getItem('user').name)
            usrName = localStorage.getItem('user').name;

        var params = {
            User: usrName,
            body: this.state.userComment,
            article: this.props.articleID
        };

        fetch('http://localhost:3000/comments/'+this.props.articleID+'', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(params)
        }).then(res => res.json())
        .then((data) => {
            this.setState({userName: localStorage.getItem('user').name, userComment: ''});
            document.getElementById("commentForm").reset();
            this.props.action(params);
        })
        .catch(console.log)

    }
    render() {
        if (localStorage.getItem('token')){
            return (
                <div className="row__wrapper form__wrapper">
                    <p className="form__title">Deja tu comentario</p>
                    <form id="commentForm" onSubmit={this.handleSubmit}>
                        <input name="userName" type="hidden" value={ this.state.userName } />
                        <textarea name="userComment" value={this.state.userComment} onChange={this.handleChange}></textarea>
                        <input name="articleId" type="hidden" value={ this.props.articleID } />
                        <input type="submit" value="submit" />
                    </form>
                    <div class="not__me">
                        <p>¿No eres tu?</p>
                        <Logout />
                    </div>
                </div>
            );
        } else {
            return (
                <div className="row__wrapper form__wrapper">
                    <p className="form__title">Para comentar debes logearte</p>
                    <Login action={this.handleLogin.bind(this)} />
                </div>
            )
        }
    }
}

export default CommentForm
